javascript:
// This is used to avoid csrf token error
frappe.csrf_token = document.getElementById('csrf_token').value;
$.ajaxSetup({ headers: { 'X-Frappe-CSRF-Token': frappe.csrf_token } });

if(frappe.realtime){
    frappe.realtime.init()
}

frappe.realtime.on("new_kitchen_order", function(data){
    appendOrderCard(data)
})

function appendOrderCard(order){
    const kitchen = document.getElementById(`order-${order.status.toLowerCase()}`)
    if(kitchen){
        kitchen.innerHTML += cardTemplate(order)
    }
}

// template for each orders
const cardTemplate = (order) => `
    <div class="order-card" onclick="openPopup('${order.name}', '${order.status}')">
      <b>#${order.name}</b><br>
        <small>${order.pos_invoice || ''}</small>
    </div>
`;

function renderOrders(orders) {
    const columns = ['Received', 'Preparing', 'Ready'];

    columns.forEach(status => {
        const filtered = orders.filter(o => o.status === status);
        const container = document.getElementById(`order-${status.toLowerCase()}`);

        if (container) {
            container.innerHTML = filtered.map(cardTemplate).join("");
        }
    });
}

// fetching data from kitchen-order lists
function refresh() {
    frappe.call({
        method: "frappe.client.get_list",
        args: {
            doctype: "Kitchen Order",
            fields: ["name", "status", "pos_invoice"],
            filters: { status: ["not in", ["Served"]] }
        },
        callback: (res) => renderOrders(res.message || [])
    });
}

// when clicks then list open popum
function openPopup(id, status) {

    const modal = document.getElementById('modal-container');
    document.getElementById('order-id').innerText = `Order #${id}`;
    document.getElementById('order-status').innerText = status;
    modal.style.display = 'flex';

    document.getElementById('btn-next').onclick = () => {

        const stages = ['Received', 'Preparing', 'Ready', 'Served'];
        const nextStatus = stages[stages.indexOf(status) + 1];

        frappe.call({
            method: "frappe.client.set_value",
            args: { 
                doctype: "Kitchen Order", 
                name: id, 
                fieldname: "status", 
                value: nextStatus 
            },
            callback: () => {
                closeModal();
                refresh();
            }
        });
    };
}


const closeModal = () => {
    document.getElementById('modal-container').style.display = 'none';
};


refresh();

if (frappe.realtime) {
    frappe.realtime.init();
}

// this is used to automatically fetch new listed orders using socket.io
frappe.realtime.on("new_pharmacy_order", function(data){
    appendOrderCard(data)
})

function appendOrderCard(order){
    const container = document.getElementById(`order-${order.status.toLowerCase()}`)
    if(container){
        container.innerHTML += cardTemplate(order)
    }

    const count = document.getElementById(`count-${order.status.toLowerCase()}`)
    if(count){
        count.innerText = parseInt(count.innerText || 0)+1
    }
}

// This is used to avoid csrf token error
frappe.csrf_token = document.getElementById('csrf_token').value;
$.ajaxSetup({ headers: { 'X-Frappe-CSRF-Token': frappe.csrf_token } });


function refresh(){
    frappe.call({
        method: "frappe.client.get_list",
        args: {
            doctype: "Pharmacy Order POS",
            fields: ["name", "status", "pharmacy_pos_invoice"],
            filters: { status: ["not in", ["Supplied"]] }
        },
        callback: (res) => getOrder(res.message || [])
    })
}

function getOrder(orders){
    const stages = ["Received", "Collected", "Ready"];

    stages.forEach(status => {
        const filtered = orders.filter(o => o.status === status)

        const length = filtered.length
        const count = document.getElementById(`count-${status.toLowerCase()}`)
        if(count){
            count.innerText = length;
        }

        const container = document.getElementById(`order-${status.toLowerCase()}`)
        if(container){
            container.innerHTML = filtered.map(item => cardTemplate(item)).join("")
        }
    })
}

const cardTemplate = (order) =>`
<div class="order-card" onClick="openModal('${order.name}','${order.status}','${order.pharmacy_pos_invoice}')">
 <b> ${order.name} </b> <br>
 <small> ${order.pharmacy_pos_invoice} </small>
 </div>
 `;

 function openModal(name, status, invoice){
    const modal = document.getElementById('modal-container')
    document.getElementById('order-id').innerText = `Order ${name}`
    document.getElementById('order-status').innerText = status

    modal.style.display = 'flex';

    // This call is used to get the items of the order
    frappe.call({
        method: "frappe.client.get",
        args:{
            doctype: "POS Invoice",
            name: invoice
        },
        callback: function(res){
            displayItems(res.message.items || [])
        }
    })

    document.getElementById('btn-next').onclick = () => {
        const stages = ["Received", "Collected", "Ready", "Supplied"]
        const nextStatus = stages[stages.indexOf(status) + 1]

        frappe.call({
            method: "frappe.client.set_value",
            args:{
                doctype: "Pharmacy Order POS",
                name: name,
                fieldname: "status",
                value: nextStatus
            },
            callback: () =>{ 
                closeModal();
                refresh();
            }
        })
    }
 }

 function displayItems(items){
     const container = document.getElementById("order-items")
     if(container){
         container.innerHTML = items.map(item => `
         <div class="item-row">
         <b>Item:</b> ${item.item_name} - <b>Qty:</b> ${item.qty}
         </div>
         `).join("")
     }
 }

const closeModal = () => {
    document.getElementById('modal-container').style.display = "none";
}
refresh()

frappe.csrf_token = document.getElementById('csrf_token').value;
$.ajaxSetup({ headers: { 'X-Frappe-CSRF-Token': frappe.csrf_token } });

